func:
// Invoice Information
msg.payload.type = "sendInvoice";

msg.payload.content = {
    chat_id: msg.payload.chatId,
    title : "Super Toy",
    description : "The best toy you can buy",
    payload : "secret token",
    providerToken : "",
    startParameter : "1",
    currency : "EUR", // see https://core.telegram.org/bots/payments#supported-currencies
    prices : [ {
        label : "EUR",
        amount : 150 } ], // this is 1.50EUR
}

// Optional
// msg.payload.options
//    provider_data
//    photo_url
//    photo_size
//    photo_width
//    photo_height
//    need_name
//    need_phone_number
//    need_email
//    need_shipping_address
//    send_phone_number_to_provider
//    send_email_to_provider
//    is_flexible
//    disable_notification
//    reply_to_message_id
//    reply_markup

msg.payload.options = {
    reply_to_message_id : msg.payload.messageId,
    need_name : true,
    need_phone_number : true,
    need_email : true,
    is_flexible : true
}

return msg;

// Invoice Information
msg.payload.type = "answerPreCheckoutQuery";
msg.payload.ok = true;

msg.payload.options = {
    error_message : "Product not available anymore!"
}

return msg;

if (msg.payload.type === "successful_payment") {
  msg.payload.type = "message";
  msg.payload.content = "Thank you for your purchase!";
  return msg;
}

// Invoice Information
msg.payload.type = "answerShippingQuery";
msg.payload.ok = true;

msg.payload.options = {
    error_message : "We cannot deliver to your address",
    shipping_options: [
      {
        id: "shipping",
        title: "Shipping by seller",
        prices: [
          { label: "Shipping by seller 1", amount: 500 },
          { label: "Shipping by seller 2", amount: 500 }
        ]
      },
      {
        id: "takeaway",
        title: "Take away",
        prices: [{ label: "-Take away", amount: 0 }]
      }
    ]
}

return msg;
