var allowedChatId = -1001179754637;
var allowedUserIds = [138708568];

// 1. Find out if we are in the correct chat and if the message was from admin
var fromChat = msg.payload.chatId;
if(fromChat === allowedChatId && msg.originalMessage.from !== undefined) {

    var fromUser = msg.originalMessage.from.id;
    if(allowedUserIds.indexOf(fromUser) > -1) {

        // 2. Check if admin replied to a message
        var replyToMessage = msg.originalMessage.reply_to_message;
        var replyMessage = msg.originalMessage.text;
        if(replyToMessage !== undefined){

            // 3. take some action for user
            var userToHandle = replyToMessage.from.id;

            switch(replyMessage)
            {
                case '/h':
                    node.send([msg, null, null, null, null]);
                    break;

                case '/d':
                    node.send([null, msg, null, null, null]);
                    break;

                case '/p':
                    node.send([null, null, msg, null, null]);
                    break;

                case '/r':
                    node.send([null, null, null, msg, null]);
                    break;

                default:
                    node.send([null, null, null, null, msg]);
                    break;
            }
        }
    }
}

// see also https://core.telegram.org/bots/api#sendmessage

var user = msg.originalMessage.reply_to_message.from.username;
var messageId = msg.originalMessage.reply_to_message.message_id;
msg.payload.type = 'message';
msg.payload.content = "hello " + user;
msg.payload.options = {
    reply_to_message_id : messageId
}

return msg;

// see also https://core.telegram.org/bots/api#deletemessage

var messageId = msg.originalMessage.reply_to_message.message_id;
msg.payload.type = 'deleteMessage';
msg.payload.content = messageId;
return msg;

// this lets vanish the command issued by the admin
// if you do not need it you can remove this function node 
// and the following call to the sender node, too.

var messageId = msg.originalMessage.message_id;
msg.payload.type = 'deleteMessage';
msg.payload.content = messageId;
return msg;

// see also https://core.telegram.org/bots/api#promotechatmember

// Note that it makes adifference if you are in a channel or group or super group.
// whenever you get an exception saying RIGHT_FORBIDDEN, then make sure that only valid fields are set.
// This example is tested with super group and hence can_post_messages and can_edit_messages are not set.

var user = msg.originalMessage.reply_to_message.from.id;
msg.payload.type = 'promoteChatMember';
msg.payload.content = user;
msg.payload.options = {
    is_anonymous : false,         // Pass True, if the administrator's presence in the chat is hidden
    can_manage_chat	: true,       //	Pass True, if the administrator can access the chat event log, chat statistics, message statistics in channels, see channel members, see anonymous administrators in supergroups and ignore slow mode. Implied by any other administrator privilege
    // can_post_messages : true,     // Pass True, if the administrator can create channel posts, channels only
    // can_edit_messages : true,     // Pass True, if the administrator can edit messages of other users and can pin messages, channels only
    can_delete_messages	: true,   // Pass True, if the administrator can delete messages of other users
    can_manage_voice_chats : true,// Pass True, if the administrator can manage voice chats
    can_restrict_members : true,  // Pass True, if the administrator can restrict, ban or unban chat members
    can_promote_members	: true,   // Pass True, if the administrator can add new administrators with a subset of their own privileges or demote administrators that he has promoted, directly or indirectly (promoted by administrators that were appointed by him)
    can_change_info	: true,       // Pass True, if the administrator can change chat title, photo and other settings
    can_invite_users : true,      // Pass True, if the administrator can invite new users to the chat
    can_pin_messages : true,      // Pass True, if the administrator can pin messages, supergroups only    
};

return msg;

var user = msg.originalMessage.reply_to_message.from.id;
msg.payload.type = 'restrictChatMember';
msg.payload.content = user;
msg.payload.options = {
    can_send_messages : false,         // Optional. True, if the user is allowed to send text messages, contacts, locations and venues
    can_send_media_messages : false,   // Optional. True, if the user is allowed to send audios, documents, photos, videos, video notes and voice notes, implies can_send_messages
    can_send_polls : false,            // Optional. True, if the user is allowed to send polls, implies can_send_messages
    can_send_other_messages	: false,   // Optional. True, if the user is allowed to send animations, games, stickers and use inline bots, implies can_send_media_messages
    can_add_web_page_previews : false, // Optional. True, if the user is allowed to add web page previews to their messages, implies can_send_media_messages
    can_change_info : false,           // Optional. True, if the user is allowed to change the chat title, photo and other settings. Ignored in public supergroups
    can_invite_users : false,          // Optional. True, if the user is allowed to invite new users to the chat
    can_pin_messages : false,          // Optional. True, if the user is allowed to pin messages. Ignored in public supergroups   
};
return msg;
